-- ===========================================================
-- TABLE: order_details
-- Stores product-level details for each order
-- ===========================================================

CREATE TABLE order_details (
    OrderDetailID INT PRIMARY KEY,   -- Unique row ID
    OrderID INT NOT NULL,            -- Order reference (FK to orders)
    ProductID INT NOT NULL,          -- Product reference (FK to products)
    Quantity INT NOT NULL,            -- Ordered quantity
    FOREIGN KEY (OrderID) REFERENCES orders(OrderID),
    FOREIGN KEY (ProductID) REFERENCES products(ProductID)
);

